+            author: obs.author ?? null,
+            resolved: obs.resolved,
+            resolution: obs.resolution ?? null,
+            resolved_at: obs.resolved_at ?? null,
+            resolved_by: obs.resolved_by ?? null,
+            promoted_to: obs.promoted_to ?? null,
