  1191â†’+/**
  1192â†’+ * Run a kspec CLI command and return stdout
  1193â†’+ */
  1194â†’+function kspec(args: string, cwd: string): string {
  1195â†’+  const cmd = `npx tsx ${CLI_PATH} ${args}`;
  1196â†’+  try {
  1197â†’+    return execSync(cmd, {
  1198â†’+      cwd,
  1199â†’+      encoding: 'utf-8',
  1200â†’+      env: { ...process.env, KSPEC_AUTHOR: '@test' },
  1201â†’+    }).trim();
  1202â†’+  } catch (error: unknown) {
  1203â†’+    const execError = error as { stdout?: string; stderr?: string; message?: string };
  1204â†’+    // Return stdout even on error (some commands exit non-zero with valid output)
  1205â†’+    if (execError.stdout) return execError.stdout.trim();
  1206â†’+    throw new Error(`Command failed: ${cmd}\n${execError.stderr || execError.message}`);
  1207â†’+  }
  1208â†’+}
  1209â†’+
  1210â†’+/**
  1211â†’+ * Run kspec and return JSON output
  1212â†’+ */
  1213â†’+function kspecJson<T>(args: string, cwd: string): T {
  1214â†’+  const output = kspec(`${args} --json`, cwd);
  1215â†’+  return JSON.parse(output);
  1216â†’+}
