        const table = new Table({
          head: [chalk.bold('ID'), chalk.bold('Type'), chalk.bold('Context')],
          style: {
            head: [],
            border: [],
          },
        });

        for (const item of items) {
          table.push([item.id, item.type, item.context]);
        }

        console.log(table.toString());
      });
    } catch (err) {
      error('Failed to list meta items', err);
      process.exit(1);
    }
  });

