            name: options.name,
            description: options.description || '',
            capabilities: options.capability || [],
            tools: options.tool || [],
            conventions: options.convention || [],
          };
        } else if (type === 'workflow') {
          // Validate required fields
          if (!options.id) {
            error(errors.validation.workflowRequiresId);
            process.exit(1);
          }
          if (!options.trigger) {
            error(errors.validation.workflowRequiresTrigger);
            process.exit(1);
          }

          item = {
            _ulid: itemUlid,
            id: options.id,
            trigger: options.trigger,
            description: options.description || '',
            steps: [],
          };
        } else {
          // convention
          if (!options.domain) {
            error(errors.validation.conventionRequiresDomain);
            process.exit(1);
          }

          item = {
            _ulid: itemUlid,
            domain: options.domain,
            rules: options.rule || [],
            examples: [],
          };
        }

        // Save the item
        await saveMetaItem(ctx, item, type as 'agent' | 'workflow' | 'convention');

        if (isJsonMode()) {
