   831â†’    .command('resolve <ref> [resolution]')
   832â†’    .description('Resolve an observation')
   833â†’    .action(async (ref: string, resolution: string | undefined) => {
   834â†’      try {
   835â†’        const ctx = await initContext();
   836â†’
   837â†’        if (!ctx.manifestPath) {
   838â†’          error(errors.project.noKspecProject);
   839â†’          process.exit(1);
   840â†’        }
   841â†’
   842â†’        const metaCtx = await loadMetaContext(ctx);
   843â†’        const observations = metaCtx.observations || [];
   844â†’
   845â†’        // Find observation
   846â†’        const normalizedRef = ref.startsWith('@') ? ref.substring(1) : ref;
   847â†’        const observation = observations.find((o) => o._ulid.startsWith(normalizedRef));
   848â†’
   849â†’        if (!observation) {
   850â†’          error(errors.reference.observationNotFound(ref));
   851â†’          process.exit(1);
   852â†’        }
   853â†’
   854â†’        // AC-obs-7: Check if already resolved
   855â†’        if (observation.resolved) {
   856â†’          const resolvedDate = new Date(observation.resolved_at!).toISOString().split('T')[0];
   857â†’          const resolutionText = observation.resolution || '';
   858â†’          const truncated = resolutionText.length > 50
   859â†’            ? resolutionText.substring(0, 50) + '...'
   860â†’            : resolutionText;
   861â†’          error(errors.conflict.observationAlreadyResolved(resolvedDate, truncated));
   862â†’          process.exit(1);
   863â†’        }
   864â†’
   865â†’        // AC-obs-9: Auto-populate resolution from task completion if promoted
   866â†’        let finalResolution = resolution;
   867â†’        if (!finalResolution && observation.promoted_to) {
   868â†’          // Fetch task to get completion reason
   869â†’          const tasks = await loadAllTasks(ctx);
   870â†’          const items = await loadAllItems(ctx);
   871â†’          const index = new ReferenceIndex(tasks, items);
   872â†’          const taskResult = index.resolve(observation.promoted_to);
   873â†’
   874â†’          if (taskResult.ok) {
   875â†’            const item = taskResult.item;
   876â†’            // Type guard: ensure this is a task (has status and depends_on properties)
   877â†’            if ('status' in item && 'depends_on' in item) {
   878â†’              const task = item as LoadedTask;
   879â†’              if (task.status === 'completed' && task.closed_reason) {
   880â†’                finalResolution = `Resolved via task ${observation.promoted_to}: ${task.closed_reason}`;
   881â†’              } else if (task.status === 'completed') {
   882â†’                finalResolution = `Resolved via task ${observation.promoted_to}`;
   883â†’              } else {
   884â†’                error(`Task ${observation.promoted_to} is not completed yet`);
   885â†’                process.exit(1);
   886â†’              }
   887â†’            } else {
   888â†’              error(`Reference ${observation.promoted_to} is not a task`);
   889â†’              process.exit(1);
   890â†’            }
   891â†’          } else {
   892â†’            error(`Task ${observation.promoted_to} not found`);
   893â†’            process.exit(1);
   894â†’          }
   895â†’        }
   896â†’
   897â†’        if (!finalResolution) {
   898â†’          error(errors.validation.resolutionRequired);
   899â†’          process.exit(1);
   900â†’        }
   901â†’
   902â†’        // AC-obs-4: Update observation
