   382â†’                    clientOptions: {
   383â†’                      clientInfo: {
   384â†’                        name: 'kspec-ralph',
   385â†’                        version: '0.1.0',
   386â†’                      },
   387â†’                    },
   388â†’                  });
   389â†’
   390â†’                  // Set up streaming update handler with translator + renderer
   391â†’                  agent.client.on('update', (_sid: string, update: SessionUpdate) => {
   392â†’                    // Translate ACP event to RalphEvent and render
   393â†’                    const event = translator.translate(update);
   394â†’                    if (event) {
   395â†’                      renderer.render(event);
   396â†’                    }
   397â†’                    // Log raw update event (async, non-blocking)
   398â†’                    appendEvent(specDir, {
   399â†’                      session_id: sessionId,
   400â†’                      type: 'session.update',
   401â†’                      data: { iteration, update },
   402â†’                    }).catch(() => {
   403â†’                      // Ignore logging errors during streaming
   404â†’                    });
   405â†’                  });
   406â†’
   407â†’                  // Set up tool request handler
   408â†’                  agent.client.on('request', (reqId: string | number, method: string, params: unknown) => {
   409â†’                    handleRequest(agent!.client, reqId, method, params, options.yolo).catch((err) => {
   410â†’                      agent!.client.respondError(reqId, -32000, err.message);
   411â†’                    });
   412â†’                  });
   413â†’                }
   414â†’
   415â†’                // Create fresh ACP session per iteration to keep context clean
   416â†’                info('Creating ACP session...');
   417â†’                acpSessionId = await agent.client.newSession({
   418â†’                  cwd: process.cwd(),
   419â†’                  mcpServers: [], // No MCP servers for now
   420â†’                });
   421â†’
   422â†’                info('Sending prompt to agent...');
   423â†’
   424â†’                // Send prompt and wait for completion
   425â†’                const response = await agent.client.prompt({
   426â†’                  sessionId: acpSessionId!,
   427â†’                  prompt: [{ type: 'text', text: prompt }],
   428â†’                });
   429â†’
   430â†’                // Log completion
   431â†’                await appendEvent(specDir, {
   432â†’                  session_id: sessionId,
   433â†’                  type: 'session.update',
   434â†’                  data: {
   435â†’                    iteration,
   436â†’                    stopReason: response.stopReason,
   437â†’                    completed: true,
   438â†’                  },
   439â†’                });
   440â†’
   441â†’                // Check stop reason
   442â†’                if (response.stopReason === 'cancelled') {
   443â†’                  throw new Error(errors.usage.agentPromptCancelled);
   444â†’                }
   445â†’
   446â†’                succeeded = true;
   447â†’                break;
   448â†’              } catch (err) {
   449â†’                lastError = err as Error;
   450â†’                error(errors.failures.iterationFailed(lastError.message));
   451â†’
   452â†’                // Clean up agent on error - will respawn next attempt
   453â†’                if (agent) {
   454â†’                  agent.kill();
