   457â†’                }
   458â†’              }
   459â†’            }
   460â†’
   461â†’            if (succeeded) {
   462â†’              success(`Completed iteration ${iteration}`);
   463â†’              consecutiveFailures = 0;
   464â†’              console.log(); // Newline after streaming output
   465â†’            } else {
   466â†’              consecutiveFailures++;
   467â†’              error(errors.failures.iterationFailedAfterRetries(iteration, maxRetries, consecutiveFailures, maxFailures));
   468â†’              if (lastError) {
   469â†’                error(errors.failures.lastError(lastError.message));
   470â†’              }
   471â†’
   472â†’              if (consecutiveFailures >= maxFailures) {
   473â†’                error(errors.failures.reachedMaxFailures(maxFailures));
   474â†’                break;
   475â†’              }
   476â†’
   477â†’              info('Continuing to next iteration...');
   478â†’            }
   479â†’          }
   480â†’        } finally {
