   493â†’              consecutiveFailures,
   494â†’            },
   495â†’          });
   496â†’          await updateSessionStatus(specDir, sessionId, status);
   497â†’        }
   498â†’
   499â†’        console.log(chalk.green(`\n${'â”€'.repeat(60)}`));
   500â†’        success('Ralph loop completed');
   501â†’        console.log(chalk.green(`${'â”€'.repeat(60)}\n`));
   502â†’
   503â†’      } catch (err) {
