                  t._ulid === updatedTask._ulid ? { ...t, ...updatedTask } : t
                );
                const syncResult = await syncSpecImplementationStatus(
                  ctx,
                  updatedTask as LoadedTask,
                  updatedTasks as LoadedTask[],
                  items,
                  index
