import { z } from 'zod';

// Common types used across spec and task schemas

/**
 * ULID pattern - 26 character Crockford base32
 * Excludes I, L, O, U to avoid confusion with 1, 1, 0, V
 */
export const ulidPattern = /^[0-9A-HJKMNP-TV-Z]{26}$/i;

/**
 * Slug pattern - lowercase alphanumeric with hyphens
 */
export const slugPattern = /^[a-z][a-z0-9-]*$/;

/**
 * Reference pattern - @ prefix followed by slug or ULID (or short ULID)
 */
export const refPattern = /^@[a-zA-Z0-9-]+$/;

// Base schemas
export const UlidSchema = z.string().regex(ulidPattern, 'Invalid ULID format');
export const SlugSchema = z.string().regex(slugPattern, 'Invalid slug format');
export const RefSchema = z.string().regex(refPattern, 'Invalid reference format');

// Priority can be string or number
export const PrioritySchema = z.union([
  z.enum(['high', 'medium', 'low']),
  z.number().int().min(1).max(5),
]);

// ISO 8601 date or datetime
export const DateTimeSchema = z.union([
  z.string().datetime(),
  z.string().date(),
]);

// Maturity status
export const MaturitySchema = z.enum(['draft', 'proposed', 'stable', 'deferred', 'deprecated']);

// Implementation status
export const ImplementationStatusSchema = z.enum([
  'not_started',
  'in_progress',
  'implemented',
  'verified',
]);

// Task status
export const TaskStatusSchema = z.enum([
  'pending',
  'in_progress',
  'pending_review',
  'blocked',
  'completed',
  'cancelled',
]);

// Task type
export const TaskTypeSchema = z.enum([
  'epic',
  'task',
  'bug',
  'spike',
  'infra',
]);

// Item type
export const ItemTypeSchema = z.enum([
  'module',
  'feature',
  'requirement',
  'constraint',
  'decision',
  'task',
]);

// VCS reference
export const VcsRefSchema = z.object({
  ref: z.string(),
  type: z.enum(['branch', 'tag', 'commit']).optional(),
});

export type Ulid = z.infer<typeof UlidSchema>;
export type Slug = z.infer<typeof SlugSchema>;
export type Ref = z.infer<typeof RefSchema>;
export type Priority = z.infer<typeof PrioritySchema>;
export type DateTime = z.infer<typeof DateTimeSchema>;
export type Maturity = z.infer<typeof MaturitySchema>;
export type ImplementationStatus = z.infer<typeof ImplementationStatusSchema>;
export type TaskStatus = z.infer<typeof TaskStatusSchema>;
export type TaskType = z.infer<typeof TaskTypeSchema>;
export type ItemType = z.infer<typeof ItemTypeSchema>;
export type VcsRef = z.infer<typeof VcsRefSchema>;

import { z } from 'zod';
import {
  UlidSchema,
  SlugSchema,
  RefSchema,
  PrioritySchema,
  DateTimeSchema,
  MaturitySchema,
  ImplementationStatusSchema,
  ItemTypeSchema,
} from './common.js';
import { NoteSchema } from './task.js';

/**
 * Status block for spec items
 */
export const StatusSchema = z.object({
  maturity: MaturitySchema.default('draft'),
  implementation: ImplementationStatusSchema.default('not_started'),
});

/**
 * Acceptance criteria in Given/When/Then format
 */
export const AcceptanceCriterionSchema = z.object({
  id: z.string(),
  given: z.string(),
  when: z.string(),
  then: z.string(),
});

/**
 * Implementation traceability
 */
export const ImplementationRefSchema = z.object({
  path: z.string(),
  function: z.string().optional(),
  lines: z.string().optional(),
});

/**
 * Traceability block
 */
export const TraceabilitySchema = z.object({
  implementation: z.array(ImplementationRefSchema).optional(),
  tests: z.array(z.object({ path: z.string() })).optional(),
  commits: z.array(z.string()).optional(),
  issues: z.array(z.string()).optional(),
});

/**
 * Full spec item schema
 */
export const SpecItemSchema = z.object({
  // Identity
  _ulid: UlidSchema,
  slugs: z.array(SlugSchema).default([]),
  title: z.string().min(1, 'Title is required'),
  type: ItemTypeSchema.optional(),

  // Status
  status: StatusSchema.optional(),

  // Classification
  priority: PrioritySchema.optional(),
  tags: z.array(z.string()).default([]),

  // Content
  description: z.string().optional(),
  acceptance_criteria: z.array(AcceptanceCriterionSchema).optional(),

  // Relationships (references start with @)
  depends_on: z.array(RefSchema).default([]),
  implements: z.array(RefSchema).default([]),
  relates_to: z.array(RefSchema).default([]),
  tests: z.array(RefSchema).default([]),
  supersedes: RefSchema.nullable().optional(),

  // Traceability
  traceability: TraceabilitySchema.optional(),

  // Lifecycle
  created: DateTimeSchema.optional(),
  created_by: z.string().optional(),
  deprecated_in: z.string().nullable().optional(),
  superseded_by: RefSchema.nullable().optional(),

  // Notes (work log / implementation context)
  notes: z.array(NoteSchema).default([]),
});

/**
 * Spec item input schema (for creating new items, ULID auto-generated)
 */
export const SpecItemInputSchema = SpecItemSchema.omit({ _ulid: true }).extend({
  _ulid: UlidSchema.optional(),
});

/**
 * Spec item patch schema (partial fields, passthrough for unknown)
 * Used by `kspec item patch` for JSON updates
 */
export const SpecItemPatchSchema = SpecItemInputSchema.partial().passthrough();

/**
 * Root manifest schema
 */
export const ManifestSchema = z.object({
  kynetic: z.string().default('1.0'),
  project: z.object({
    name: z.string(),
    version: z.string().default('0.1.0'),
    status: MaturitySchema.default('draft'),
  }),

  // Inline items (small projects)
  modules: z.array(z.any()).optional(), // Recursive, define separately if needed
  features: z.array(z.any()).optional(),
  requirements: z.array(z.any()).optional(),
  constraints: z.array(z.any()).optional(),
  decisions: z.array(z.any()).optional(),

  // External references (large projects)
  includes: z.array(z.string()).optional(),

  // Hooks configuration
  hooks: z.record(z.string()).optional(),
});

export type Status = z.infer<typeof StatusSchema>;
export type AcceptanceCriterion = z.infer<typeof AcceptanceCriterionSchema>;
export type ImplementationRef = z.infer<typeof ImplementationRefSchema>;
export type Traceability = z.infer<typeof TraceabilitySchema>;
export type SpecItem = z.infer<typeof SpecItemSchema>;
export type SpecItemInput = z.infer<typeof SpecItemInputSchema>;
export type SpecItemPatch = z.infer<typeof SpecItemPatchSchema>;
export type Manifest = z.infer<typeof ManifestSchema>;

