   128→/**
   129→ * Start the daemon server
   130→ * AC: @cli-serve-commands ac-1 (foreground), ac-2 (daemon), ac-3 (port), ac-10 (port error)
   131→ */
   132→async function startServer(opts: {
   133→  daemon?: boolean;
   134→  port: string;
   135→  kspecDir: string;
   136→}): Promise<void> {
   137→  const port = parseInt(opts.port, 10);
   138→  const jsonMode = isJsonMode();
   139→
   140→  // AC: @cli-serve-commands ac-10
   141→  if (isNaN(port) || port < 1 || port > 65535) {
   142→    if (jsonMode) {
   143→      output({
   144→        error: 'Invalid port number. Must be between 1 and 65535.',
   145→        hint: 'Try: kspec serve --port <PORT>',
   146→      });
   147→    } else {
   148→      error('Invalid port number. Must be between 1 and 65535.');
   149→      error('Try: kspec serve --port <PORT>');
   150→    }
   151→    process.exit(EXIT_CODES.VALIDATION_FAILED);
   152→  }
   153→
   154→  const pidManager = new PidFileManager(opts.kspecDir);
   155→
   156→  // Check if already running
   157→  if (pidManager.isDaemonRunning()) {
   158→    const pid = pidManager.read();
   159→    if (isJsonMode()) {
   160→      output({ running: true, pid, message: 'Daemon already running' });
   161→    } else {
   162→      warn(`Daemon already running with PID ${pid}`);
   163→    }
   164→    process.exit(EXIT_CODES.SUCCESS);
   165→  }
   166→
   167→  // Get path to daemon entry point - resolve from cwd to handle both dev and built scenarios
   168→  const daemonBinary = join(process.cwd(), 'packages/daemon/src/index.ts');
   169→
   170→  if (!existsSync(daemonBinary)) {
   171→    if (isJsonMode()) {
   172→      output({ error: `Daemon binary not found at: ${daemonBinary}`, hint: 'Ensure you are running from the project root' });
   173→    } else {
   174→      error(`Daemon binary not found at: ${daemonBinary}`);
   175→      error('Ensure you are running from the project root');
   176→    }
   177→    process.exit(EXIT_CODES.ERROR);
   178→  }
   179→
   180→  // AC: @cli-serve-commands ac-2 - background mode
   181→  if (opts.daemon) {
   182→    // Write port config for restart persistence (AC: @cli-serve-commands ac-7)
   183→    writeDaemonPort(opts.kspecDir, opts.port);
   184→
   185→    // Spawn detached process
   186→    const child = spawn('bun', [daemonBinary, '--port', opts.port, '--kspec-dir', opts.kspecDir], {
   187→      detached: true,
   188→      stdio: 'ignore', // TODO: redirect to log file when logging implemented
   189→      cwd: process.cwd(),
   190→    });
   191→
   192→    // Detach from parent
   193→    child.unref();
   194→
   195→    // Poll for PID file with timeout (max 5 seconds)
   196→    const maxWait = 5000;
   197→    const startTime = Date.now();
   198→    let pid: number | null = null;
   199→
   200→    while (Date.now() - startTime < maxWait) {
   201→      pid = pidManager.read();
   202→      if (pid && pidManager.isDaemonRunning()) {
   203→        break;
   204→      }
   205→      await new Promise(resolve => setTimeout(resolve, 100));
   206→    }
   207→
   208→    if (pid && pidManager.isDaemonRunning()) {
   209→      if (isJsonMode()) {
   210→        output({ running: true, pid, port });
   211→      } else {
   212→        success(`Daemon started with PID ${pid} on port ${port}`);
   213→        output({ running: true, pid, port });
   214→      }
   215→    } else {
   216→      if (isJsonMode()) {
   217→        output({ error: 'Daemon failed to start within 5 seconds' });
   218→      } else {
   219→        error('Daemon failed to start within 5 seconds');
   220→      }
   221→      process.exit(EXIT_CODES.ERROR);
   222→    }
   223→  } else {
   224→    // AC: @cli-serve-commands ac-1 - foreground mode
   225→    if (!isJsonMode()) {
   226→      info(`Starting server in foreground on port ${port}...`);
   227→      info('Press Ctrl+C to stop');
   228→    }
   229→
   230→    const child = spawn('bun', [daemonBinary, '--port', opts.port, '--kspec-dir', opts.kspecDir], {
   231→      stdio: 'inherit',
   232→      cwd: process.cwd(),
   233→    });
   234→
   235→    // Handle Ctrl+C - forward SIGTERM to child for graceful shutdown
   236→    process.on('SIGINT', () => {
   237→      if (!isJsonMode()) {
   238→        info('\nStopping server...');
   239→      }
   240→      child.kill('SIGTERM');
   241→
   242→      // Wait for graceful shutdown (max 5 seconds)
   243→      const shutdownTimeout = setTimeout(() => {
   244→        child.kill('SIGKILL'); // Force kill if not stopped
   245→      }, 5000);
   246→
   247→      child.on('exit', () => {
   248→        clearTimeout(shutdownTimeout);
   249→      });
   250→    });
   251→
   252→    // Wait for process to exit
   253→    child.on('exit', (code) => {
   254→      process.exit(code ?? 0);
   255→    });
   256→  }
   257→}
   258→
   259→/**
   260→ * Stop the daemon server
   261→ * AC: @cli-serve-commands ac-4 (stop), ac-5 (idempotent)
   262→ */
   263→async function stopServer(opts: { kspecDir: string; json?: boolean }): Promise<void> {
   264→  if (isJsonMode()) {
   265→  }
   266→
   267→  const pidManager = new PidFileManager(opts.kspecDir);
   268→
   269→  if (!pidManager.isDaemonRunning()) {
   270→    // AC: @cli-serve-commands ac-5
   271→    if (isJsonMode()) {
   272→      output({ running: false });
   273→    } else {
   274→      info('Daemon not running');
   275→      output({ running: false });
   276→    }
   277→    process.exit(EXIT_CODES.SUCCESS);
   278→  }
   279→
   280→  const pid = pidManager.read();
   281→  if (!pid) {
   282→    if (isJsonMode()) {
   283→      output({ error: 'Failed to read PID file' });
   284→    } else {
   285→      error('Failed to read PID file');
   286→    }
   287→    process.exit(EXIT_CODES.ERROR);
   288→  }
   289→
   290→  // AC: @cli-serve-commands ac-4
   291→  if (!isJsonMode()) {
   292→    info(`Stopping daemon (PID ${pid})...`);
   293→  }
   294→
   295→  try {
   296→    // Send SIGTERM
   297→    process.kill(pid, 'SIGTERM');
   298→
   299→    // Wait for clean shutdown (max 5 seconds)
   300→    const maxWait = 5000;
   301→    const startTime = Date.now();
   302→    while (Date.now() - startTime < maxWait) {
   303→      if (!pidManager.isDaemonRunning()) {
   304→        break;
   305→      }
   306→      await new Promise(resolve => setTimeout(resolve, 100));
   307→    }
   308→
   309→    if (pidManager.isDaemonRunning()) {
   310→      if (!isJsonMode()) {
   311→        warn(`Daemon did not stop gracefully, forcing...`);
   312→      }
   313→      process.kill(pid, 'SIGKILL');
   314→      await new Promise(resolve => setTimeout(resolve, 500));
   315→    }
   316→
   317→    if (isJsonMode()) {
   318→      output({ stopped: true, pid });
   319→    } else {
   320→      success('Daemon stopped');
   321→      output({ stopped: true, pid });
   322→    }
   323→  } catch (err) {
   324→    if (isJsonMode()) {
   325→      output({ error: err instanceof Error ? err.message : String(err) });
   326→    } else {
   327→      error(`Failed to stop daemon: ${err instanceof Error ? err.message : String(err)}`);
   328→    }
   329→    process.exit(EXIT_CODES.ERROR);
   330→  }
   331→}
   332→
   333→/**
   334→ * Check daemon server status
   335→ * AC: @cli-serve-commands ac-6
   336→ */
   337→async function statusServer(opts: { kspecDir: string; json?: boolean }): Promise<void> {
   338→  if (isJsonMode()) {
   339→  }
   340→
   341→  const pidManager = new PidFileManager(opts.kspecDir);
   342→  const running = pidManager.isDaemonRunning();
   343→  const pid = pidManager.read();
   344→
   345→  // Read port from config (AC: @cli-serve-commands ac-6)
   346→  const port = readDaemonPort(opts.kspecDir);
   347→
   348→  // TODO: Fetch uptime, connections from health endpoint when implemented
   349→  const status = {
   350→    running,
   351→    pid: pid ?? null,
   352→    port: port ? parseInt(port, 10) : null,
   353→    uptime: null, // TODO: fetch from health endpoint
   354→    connections: null, // TODO: fetch from health endpoint
   355→  };
   356→
   357→  if (isJsonMode()) {
   358→    output(status);
   359→  } else {
   360→    if (running) {
   361→      output(`Daemon running (PID: ${pid})`);
   362→    } else {
   363→      output('Daemon not running');
   364→    }
   365→    output(status);
   366→  }
   367→}
   368→
   369→/**
   370→ * Restart the daemon server
   371→ * AC: @cli-serve-commands ac-7
   372→ */
   373→async function restartServer(opts: { kspecDir: string; json?: boolean }): Promise<void> {
   374→  if (isJsonMode()) {
   375→  }
   376→
   377→  const pidManager = new PidFileManager(opts.kspecDir);
   378→
   379→  // AC: @cli-serve-commands ac-7 - preserve port across restarts
   380→  let port = readDaemonPort(opts.kspecDir) || '3456'; // use saved port or default
   381→
   382→  if (pidManager.isDaemonRunning()) {
   383→    if (!isJsonMode()) {
   384→      info('Stopping daemon...');
   385→    }
   386→    await stopServer(opts);
   387→  }
   388→
   389→  if (!isJsonMode()) {
   390→    info('Starting daemon...');
   391→  }
   392→  await startServer({ daemon: true, port, kspecDir: opts.kspecDir });
   393→}
