{
  "name": "@kynetic-ai/daemon",
  "version": "0.1.0",
  "description": "Kspec daemon server with WebSocket support",
  "type": "module",
  "main": "dist/index.js",
  "types": "dist/index.d.ts",
  "bin": {
    "kspec-daemon": "./dist/index.js"
  },
  "scripts": {
    "build": "tsc",
    "dev": "bun --watch src/index.ts",
    "start": "bun dist/index.js"
  },
  "dependencies": {
    "@elysiajs/cors": "^1.1.3",
    "@kynetic-ai/shared": "*",
    "chokidar": "^4.0.3",
    "elysia": "^1.1.31",
    "ulidx": "^2.4.1",
    "yaml": "^2.7.0"
  },
  "devDependencies": {
    "@types/bun": "latest",
    "typescript": "^5.3.3"
  }
}

