              created_at: "2024-01-10T13:00:00Z",
              author: "@test",
              content: "Made progress",
            },
          ],
        },
      ];

      const results = processFailedIteration(
        tasksInProgress,
        currentTasks,
        iterationStart,
        "Iteration failed",
      );

      // Only Task 1 should have failure tracked
      expect(results).toHaveLength(1);
      expect(results[0].taskRef).toBe("01TASK1");
    });
  });
});

