      // Check that ConnectionData type includes projectPath
      const typesContent = await readFile(
        join(process.cwd(), 'packages/daemon/src/websocket/types.ts'),
        'utf-8'
      );
      expect(typesContent).toContain('projectPath');
    });

    // AC: @multi-directory-daemon ac-21b
    it('should make project binding immutable (no mid-connection changes)', async () => {
      wsHandlerContent = await readFile(
        join(process.cwd(), 'packages/daemon/src/websocket/server.ts'),
        'utf-8'
      );

      // Verify projectPath is set once during upgrade and not modified
