      const typesContent = await readFile(
        join(process.cwd(), 'packages/daemon/src/websocket/types.ts'),
        'utf-8'
      );

      // Verify ConnectionData has projectPath field
      expect(typesContent).toContain('interface ConnectionData');
      expect(typesContent).toMatch(/projectPath.*:/);
    });
  });

  describe('Project Binding Flow', () => {
    it('should document the binding sequence in implementation', async () => {
      const wsHandlerContent = await readFile(
        join(process.cwd(), 'packages/daemon/src/websocket/server.ts'),
        'utf-8'
      );

      // The upgrade handler should:
      // 1. Read X-Kspec-Dir header
      // 2. Validate/resolve project path
      // 3. Store in connection data
      // 4. Reject if invalid

      // Verify upgrade handler exists
      expect(wsHandlerContent).toContain('upgrade');
    });
  });

  describe('Integration with PubSub', () => {
    it('should pass projectPath to PubSubManager for connection tracking', async () => {
      const wsHandlerContent = await readFile(
        join(process.cwd(), 'packages/daemon/src/websocket/server.ts'),
