    16→	let updatedTaskIds = $state<Set<string>>(new Set());
    17→
    18→	let selectedTaskId: string | null = $state(null);
    19→	let selectedTask: TaskDetailType | null = $state(null);
    20→	let detailOpen = $state(false);
    21→
    22→	// Reactive: re-fetch when URL params change
    23→	let filterParams = $derived({
    24→		status: $page.url.searchParams.get('status') || undefined,
    25→		type: $page.url.searchParams.get('type') || undefined,
    26→		tag: $page.url.searchParams.get('tag') || undefined,
    27→		assignee: $page.url.searchParams.get('assignee') || undefined,
    28→		automation: $page.url.searchParams.get('automation') || undefined,
    29→		limit: 50,
    30→		offset: 0
    31→	});
    32→
    33→	$effect(() => {
    34→		// Re-fetch when filterParams changes
    35→		filterParams;
    36→		loadTasks();
    37→	});
    38→
    39→	async function loadTasks() {
    40→		loading = true;
    41→		error = '';
    42→
    43→		try {
    44→			const response = await fetchTasks(filterParams);
    45→			tasks = response.items;
    46→			total = response.total;
    47→		} catch (err) {
    48→			error = err instanceof Error ? err.message : 'Failed to load tasks';
    49→			console.error('Error loading tasks:', err);
    50→		} finally {
    51→			loading = false;
    52→		}
    53→	}
    54→
    55→	async function handleSelectTask(event: CustomEvent<string>) {
    56→		selectedTaskId = event.detail;
    57→		try {
    58→			selectedTask = await fetchTask(selectedTaskId);
    59→			detailOpen = true;
    60→		} catch (err) {
    61→			error = err instanceof Error ? err.message : 'Failed to load task details';
    62→			console.error('Error loading task:', err);
    63→		}
    64→	}
    65→
    66→	function handleCloseDetail() {
    67→		detailOpen = false;
    68→		selectedTask = null;
    69→		selectedTaskId = null;
    70→	}
    71→
    72→	function handleUpdateTask() {
    73→		// Reload tasks to reflect changes
    74→		loadTasks();
    75→		// Reload selected task detail
    76→		if (selectedTaskId) {
    77→			fetchTask(selectedTaskId)
    78→				.then((task) => {
    79→					selectedTask = task;
    80→				})
    81→				.catch((err) => {
    82→					console.error('Error reloading task:', err);
    83→				});
    84→		}
    85→	}
    86→
    87→	// AC: @web-dashboard ac-33 - Handle WebSocket task updates
    88→	function handleTaskUpdate(event: BroadcastEvent) {
    89→		console.log('[TasksPage] Task update received:', event);
    90→
    91→		// Mark task as updated for highlight animation
    92→		if (event.data?.ulid) {
    93→			updatedTaskIds.add(event.data.ulid);
    94→			updatedTaskIds = new Set(updatedTaskIds);
    95→
    96→			// Remove highlight after 3s
    97→			setTimeout(() => {
    98→				updatedTaskIds.delete(event.data.ulid);
    99→				updatedTaskIds = new Set(updatedTaskIds);
   100→			}, 3000);
   101→		}
   102→
   103→		// Reload tasks list
   104→		loadTasks();
   105→
   106→		// Reload selected task if it's the one that updated
   107→		if (selectedTaskId && event.data?.ulid === selectedTaskId) {
   108→			fetchTask(selectedTaskId)
   109→				.then((task) => {
   110→					selectedTask = task;
   111→				})
   112→				.catch((err) => {
   113→					console.error('Error reloading task:', err);
   114→				});
   115→		}
   116→	}
   117→
   118→	onMount(() => {
   119→		loadTasks();
   120→
   121→		// AC: @web-dashboard ac-32, ac-33 - Subscribe to task updates
   122→		subscribe(['tasks']);
   123→		on('tasks', handleTaskUpdate);
   124→	});
   125→
   126→	onDestroy(() => {
   127→		// Clean up subscription
   128→		off('tasks', handleTaskUpdate);
   129→		unsubscribe(['tasks']);
   130→	});
   131→</script>
   132→
   133→<div class="flex flex-col gap-6 p-6">
   134→	<div>
   135→		<h1 class="text-3xl font-bold mb-2">Tasks</h1>
   136→		{#if !loading}
   137→			<p class="text-muted-foreground">
